var counter = msg.counter;
var sum = 0;
if(counter < msg.settings.timeAlcIn) {
    sum = counter / msg.settings.timeAlcIn * 33;
} else if (counter < msg.settings.timeAlcIn + msg.settings.timeAlcStay) {
    sum = 33 + (counter - msg.settings.timeAlcIn) / msg.settings.timeAlcStay * 33;
} else {
    sum = 66 + (counter - msg.settings.timeAlcIn - msg.settings.timeAlcStay) / msg.settings.heatingTime * 33;
}

msg.payload = sum; 
msg.counter = msg.counter + 1;
return msg;
